export default
  {
    adjustmentPrice,
    adjustmentPricePage,
    getManagePage,
    importLinhKien,
    editItemLinhKien,
    deleteItemLinhkien,
    editItemLinhKienPage,
    getNCCpage,
    importNCC,
    deleteItemNCC,
    editItemNCC,
    editItemNCCPage,
    getThuongHieupage,
    importThuongHieu,
    deleteItemThuongHieu,
    editItemThuongHieuPage,
    editItemThuongHieu
  };

