        // re_hash : bcrypt.hashSync(re_pass, saltRounds)
    }
    return {createpass} ;
}

//kiểm tra hash
const test_pass= (pass, hash) =>{
    let test =  {
        test_Hash : bcrypt.compareSync(pass, hash),  
    }
    return test ; 
}

export  {_pass, test_pass} ;

